query ContentItem ($id: ID!) {
  contentItem (id: $id) {
      id
      title
      subtitle
      content
      slug
      date (format: "D. MMMM YYYY")
      timeToRead
      keywords
      featured      
      description
      cover_image

      path
      tags {
        id
        title
        path
      }
  }
  collections: allCollection( sortBy: "slug", order: DESC, filter: { published: { eq: true } } ) {
    edges {
      node {
        id
        title
        slug
        date (format: "D. MMMM YYYY")
        timeToRead
        featured
        description
        cover_image
        path
        tags {
          id
          title
          path
        }
        offerings
      }
    }
  }
   offerings: allOffering(sort: [{ by: "price" }, { by: "boost" }] order: ASC, filter: { published: { eq: true }}) {
    edges {
      node {
        id
        title
        slug
        date (format: "D. MMMM YYYY")
        timeToRead
        featured
        price
        type
        description
        cover_image
        path
        tags {
          id
          title
          path
        }
      }
    }
  }
}
